mapper.select:
		select

		from mem_wx_share
		left join 
		(
		select inviterWxOpenId,channel,count(1) as sharesHad,ifnull(sum(assistValue),0) as assistValueHad from mem_wx_share group by inviterWxOpenId,channel
		) statisticsInfo on mem_wx_share.wxOpenId = statisticsInfo.inviterWxOpenId and mem_wx_share.channel = statisticsInfo.channel

		order by mem_wx_share.id desc
        order by ${sort} ${order}


            	limit #{offset}, #{limit}

mapper.update:
update mem_wx_share
inviterWxOpenId = #{inviterWxOpenId}, inviterWxUnionId= #{inviterWxUnionId}, wxOpenId = #{wxOpenId}, wxUnionId = #{wxUnionId}, wxHeadPhoto = #{wxHeadPhoto}, sourceId = #{sourceId}, type = #{type}, remark = #{remark}, channel = #{channel}, wxNickName = #{wxNickName}, assistValue = #{assistValue},
where id = #{id}
