mapper.select:
select
from partner_coupon left join partner_coupon_apply on partner_coupon.applyId = partner_coupon_apply.id left join partner_account on partner_coupon.applierId = partner_account.id left join partner_coupon_config on partner_coupon.configId = partner_coupon_config.id
order by partner_coupon.id order by ${sort} ${order}
limit #{offset}, #{limit}

mapper.update:
update partner_coupon
applyId = #{applyId}, configId = #{configId}, applierId = #{applierId}, couponCode = #{couponCode}, createTime = #{createTime}, updateTime = #{updateTime}, state = #{state}, orderId = #{orderId}, orderCode = #{orderCode}, effectTime = #{effectTime},
where id = #{id}
